import { useState } from "react";

function RandomList() {
  const [addNumber, setAddNumber] = useState([]);
  let numberarray = [];
  const generateRandomNum = () => {
    const random0 = Math.floor(Math.random() * 50);
    const random1 = Math.floor(Math.random() * 50);
    const random2 = Math.floor(Math.random() * 50);
    const random3 = Math.floor(Math.random() * 50);
    const random4 = Math.floor(Math.random() * 50);
    const random5 = Math.floor(Math.random() * 50);

    // console.log(random);
    numberarray = [random0, random1, random2, random3, random4, random5];
    // console.log(numberarray);

    setAddNumber(numberarray);
  };

  return (
    <>
      <div className="flex gap-1">
        {addNumber.map((item, idx) => (
          <p className="bg-red-500" key={idx}>
            {item}
          </p>
        ))}
      </div>
      <div className="flex gap-[10px]">
        <button
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
          onClick={generateRandomNum}
        >
          Show me lucky numbers
        </button>
        <button
          className="bg-transparent hover:bg-blue-500 text-blue-700 font-semibold hover:text-white py-2 px-4 border border-blue-500 hover:border-transparent rounded"
          onClick={() => {
            setAddNumber([]);
          }}
        >
          Reset
        </button>
      </div>
    </>
  );
}

export default RandomList;
